from matplotlib import pylab as plt
import nibabel as nib
import numpy as np

def draw1(loss,  name):
    x = [range(0, len(loss))]
    x = [[row[i] for row in x] for i in range(len(x[0]))]
    fig, ax = plt.subplots()
    ax.plot(x, loss, color="red", label="loss_train")
    ax.set_title(name)
    ax.legend()
    plt.show()

